import React, { useState } from 'react';
import { Routes ,Route } from 'react-router-dom';
import { FaShoppingCart } from "react-icons/fa";
import Order from './Order';
import AboutUs from './AboutUs';

const showOrders = (props) => {
    let summa = 0
    props.orders.forEach(el => summa += Number.parseFloat(el.price))
    return (<div>
        {props.orders.map(el => (
            <Order onDelete={props.onDelete} key={el.id} item={el} />
        ))}
        <p className='summa'>Сумма: {new Intl.NumberFormat().format(summa)}$</p>
    </div>)
}

const showNothing = () => {
    return (<div className='empty'>
        <h2>Товаров нет</h2>
    </div>)
}

function Header(props) {
    let [cartOpen, setCartOpen] = useState(false)

    return (
        <header>
            <div>
                <span className='logo'>Hause Staff</span>
                <Router>
                    <div>
                        <ul className='nav'>
                            <li><link to='#'>Про нас</link></li>
                            <li><link to='#'>Контакты</link></li>
                            <li><link to='#'>Кабинет</link></li>
                        </ul>
                        <Routes>
                            <Route path="/AboutUs">

                            </Route>
                        </Routes>
                    </div>
                </Router>
                <FaShoppingCart onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`shop-cart-but ${cartOpen && 'active'}`}/>

                {cartOpen && (
                    <div className='shop-cart'>
                        {props.orders.length > 0 ?
                            showOrders(props) : showNothing()}
                    </div>
                )}
            </div>
            <div className='presintation'></div>
        </header>
    );
}

export default Header;
